import React, { useState } from 'react';
import Map from './Map';
import TrendChart from './TrendChart';
import './App.css';

function App() {
  const [selectedCities, setSelectedCities] = useState([]);

  // 选择城市（最多2个）
  const handleCitySelect = (city) => {
    setSelectedCities((prev) => {
      if (prev.find((c) => c.city === city.city && c.country === city.country)) return prev;
      if (prev.length === 2) return [prev[1], city];
      return [...prev, city];
    });
  };

  return (
    <div className="App">
      <h1>World PM2.5 Visualization</h1>
      <Map onCitySelect={handleCitySelect} selectedCities={selectedCities} />
      <TrendChart selectedCities={selectedCities} />
    </div>
  );
}

export default App;
