import React, { useState } from 'react';
import Map from './Map';
import TrendChart from './TrendChart';
import MultiTrendCharts from './MultiTrendCharts';
import './App.css';

function App() {
  const [selectedCities, setSelectedCities] = useState([]);
  const [mode, setMode] = useState('compare'); // 'compare' or 'multi'

  // 选择城市
  const handleCitySelect = (city) => {
    setSelectedCities(prev => {
      if (prev.find(c => c.city === city.city && c.country === city.country)) return prev;
      const max = mode === 'multi' ? 3 : 2;
      if (prev.length === max) return prev;
      return [...prev, city];
    });
  };

  // 切换模式时清空已选城市
  const handleModeChange = (newMode) => {
    setMode(newMode);
    setSelectedCities([]);
  };

  return (
    <div className="App">
      <h1>全球城市 PM2.5 可视化 Dashboard</h1>
      <div style={{textAlign: 'center', marginBottom: 16}}>
        <button onClick={() => handleModeChange('compare')} disabled={mode === 'compare'}>对比模式</button>
        <button onClick={() => handleModeChange('multi')} disabled={mode === 'multi'}>多图模式</button>
      </div>
      <Map onCitySelect={handleCitySelect} selectedCities={selectedCities} maxCities={mode === 'multi' ? 3 : 2} />
      {mode === 'compare'
        ? <TrendChart selectedCities={selectedCities} />
        : <MultiTrendCharts selectedCities={selectedCities} />}
    </div>
  );
}

export default App;
