import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';
import * as topojson from 'topojson-client';
import NetCDFReader from './NetCDFReader';

const WORLD_GEOJSON_URL = 'world-110m.geojson';
const CITIES_JSON = 'cities_with_coords.json';
const NETCDF_URL = 'concat_weighted_output.nc';

// PM2.5色阶区间和颜色，与MultiTrendCharts.js保持一致
const bounds = [0, 5, 10, 15, 20, 30, 40, 50, 60, 70, 80, 90, 99999];
const c_list = [
  'rgb(164,255,255)',  // 0 - 5
  'rgb(176,218,233)',  // 5 - 10
  'rgb(176,206,237)',  // 10 - 15
  'rgb(249,224,71)',   // 15 - 20
  'rgb(242,200,75)',   // 20 - 30
  'rgb(241,166,63)',   // 30 - 40
  'rgb(233,135,37)',   // 40 - 50
  'rgb(175,69,83)',    // 50 - 60
  'rgb(134,59,71)',    // 60 - 70
  'rgb(103,58,61)',    // 70 - 80
  'rgb(70,47,48)',     // 80 - 90
  'rgb(37,36,36)',     // 90+
];

function getColor(val) {
  if (val === null || val === undefined || isNaN(val)) return '#e0e0e0';
  for (let i = 0; i < bounds.length - 1; ++i) {
    if (val >= bounds[i] && val < bounds[i + 1]) return c_list[i];
  }
  return c_list[c_list.length - 1];
}

function Map({ onCitySelect, selectedCities, maxCities = 2 }) {
  const svgRef = useRef();
  const [cities, setCities] = useState([]);
  const [world, setWorld] = useState(null);
  const [netcdfReader, setNetcdfReader] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // 加载数据
  useEffect(() => {
    const loadData = async () => {
      try {
        setIsLoading(true);
        
        // 并行加载地图和城市数据
        const [worldData, citiesData] = await Promise.all([
          d3.json(WORLD_GEOJSON_URL),
          d3.json(CITIES_JSON)
        ]);
        
        setWorld(worldData);
        setCities(citiesData);
        
        // 加载NetCDF数据
        const reader = new NetCDFReader();
        const success = await reader.loadData(NETCDF_URL);
        if (success) {
          setNetcdfReader(reader);
          console.log('All data loaded successfully');
        } else {
          console.warn('NetCDF data loading failed, using default colors');
        }
        
      } catch (error) {
        console.error('Error loading data:', error);
      } finally {
        setIsLoading(false);
      }
    };
    
    loadData();
  }, []);

  // 获取地理位置的PM2.5值
  const getPM25ForLocation = (lat, lon) => {
    if (!netcdfReader) return null;
    return netcdfReader.getPM25Value(lat, lon);
  };

  // 绘制地图
  useEffect(() => {
    if (!world || !cities.length || isLoading) return;
    
    const width = 1000, height = 500;
    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height);
    svg.selectAll('*').remove();

    // 投影
    const projection = d3.geoNaturalEarth1().fitSize([width, height], world);
    const path = d3.geoPath().projection(projection);

    // 绘制地图，根据PM2.5数据着色
    svg.append('g')
      .selectAll('path')
      .data(world.features)
      .enter()
      .append('path')
      .attr('d', path)
      .attr('fill', d => {
        // 获取国家/地区的中心点
        const centroid = d3.geoCentroid(d);
        const [lon, lat] = centroid;
        const pm25Value = getPM25ForLocation(lat, lon);
        return getColor(pm25Value);
      })
      .attr('stroke', '#888')
      .attr('stroke-width', 0.5)
      .append('title')
      .text(d => {
        const centroid = d3.geoCentroid(d);
        const [lon, lat] = centroid;
        const pm25Value = getPM25ForLocation(lat, lon);
        const countryName = d.properties.NAME || d.properties.name || 'Unknown';
        return pm25Value !== null 
          ? `${countryName}\nPM2.5: ${pm25Value.toFixed(1)} µg/m³`
          : `${countryName}\nPM2.5: No data`;
      });

    // 绘制城市点
    svg.append('g')
      .selectAll('circle')
      .data(cities)
      .enter()
      .append('circle')
      .attr('cx', d => projection([d.lng, d.lat])[0])
      .attr('cy', d => projection([d.lng, d.lat])[1])
      .attr('r', 4)
      .attr('fill', d => selectedCities.find(c => c.city === d.city && c.country === d.country) ? '#d32f2f' : '#1976d2')
      .attr('stroke', '#fff')
      .attr('stroke-width', 2)
      .style('cursor', 'pointer')
      .on('click', (event, d) => {
        onCitySelect(d);
      })
      .append('title')
      .text(d => {
        const pm25Value = getPM25ForLocation(d.lat, d.lng);
        return pm25Value !== null 
          ? `${d.city}, ${d.country}\nPM2.5: ${pm25Value.toFixed(1)} µg/m³`
          : `${d.city}, ${d.country}\nPM2.5: No data`;
      });

    // 添加色阶图例
    if (netcdfReader) {
      const legendWidth = 300;
      const legendHeight = 20;
      const legendX = width - legendWidth - 20;
      const legendY = height - 60;

      // 图例背景
      svg.append('rect')
        .attr('x', legendX - 10)
        .attr('y', legendY - 25)
        .attr('width', legendWidth + 20)
        .attr('height', 50)
        .attr('fill', 'white')
        .attr('stroke', '#ccc')
        .attr('rx', 5);

      // 色阶条
      const legendScale = d3.scaleLinear()
        .domain([0, bounds.length - 2])
        .range([0, legendWidth]);

      svg.append('g')
        .selectAll('rect')
        .data(c_list.slice(0, -1)) // 排除最后一个颜色（90+）
        .enter()
        .append('rect')
        .attr('x', (d, i) => legendX + legendScale(i))
        .attr('y', legendY)
        .attr('width', legendWidth / (c_list.length - 1))
        .attr('height', legendHeight)
        .attr('fill', d => d);

      // 图例标签
      svg.append('text')
        .attr('x', legendX)
        .attr('y', legendY - 5)
        .attr('font-size', 12)
        .attr('font-weight', 'bold')
        .text('PM2.5 Concentration (µg/m³) - 2022');

      // 数值标签
      const labelValues = [0, 10, 20, 30, 50, 70, 90];
      svg.append('g')
        .selectAll('text')
        .data(labelValues)
        .enter()
        .append('text')
        .attr('x', d => {
          const index = bounds.findIndex(b => b >= d);
          return legendX + legendScale(index);
        })
        .attr('y', legendY + legendHeight + 15)
        .attr('text-anchor', 'middle')
        .attr('font-size', 10)
        .text(d => d);
    }
  }, [world, cities, selectedCities, onCitySelect, maxCities, netcdfReader, isLoading]);

  return (
    <div style={{ textAlign: 'center' }}>
      {isLoading && (
        <div style={{ margin: 20, color: '#666' }}>
          Loading PM2.5 data and map...
        </div>
      )}
      <svg ref={svgRef}></svg>
      <div style={{marginTop: 8, color: '#888'}}>
        {netcdfReader ? 
          '* Map colored by PM2.5 concentration (2022). Click cities to view trends.' :
          '* Loading PM2.5 data for map coloring...'
        }
      </div>
    </div>
  );
}

export default Map;
